<?phpfunction listing_membres2() {	$membres = array();	$pdo = PDO2::getInstance();	$requete = $pdo->prepare("SELECT * FROM membres");	$requete->execute();	while ($result = $requete->fetch(PDO::FETCH_ASSOC)) {		$membres[] = $result;	}	$requete->closeCursor();		return $membres;}function detailsMembre($id) {	$pdo = PDO2::getInstance();		$requete = $pdo->prepare("SELECT * FROM membres where id = :id_utilisateur");	$requete->bindValue(':id_utilisateur', $id);		$requete->execute();			if ($result = $requete->fetch(PDO::FETCH_ASSOC)) {			$requete->closeCursor();		return $result;	}	return false;}class membre {	private $id;	private $nom;	private $prenom;	private $nomMoto;	private $marqueMoto;	private $typeMoto;	private $roleAsso;	private $dateNaissance;	private $photo;	private $sexe;	private $typeMembre;	private $dateCreation;	private $dateMaj;			public function __construct($id) 	{		$this->id = $id;	}		public function getId() {		return $this->id;	}	public function load() {		$pdo = PDO2::getInstance();				$requete = $pdo->prepare("SELECT * FROM membres where id = :id_utilisateur");		$requete->bindValue(':id_utilisateur', $this->id);			$requete->execute();		if ($result = $requete->fetch(PDO::FETCH_ASSOC)) {			$requete->closeCursor();			$this->nom = utf8_encode($result['nom']);			$this->prenom = utf8_encode($result['prenom']);			$this->nomMoto = utf8_encode($result['nom_moto']);			$this->marqueMoto = utf8_encode($result['marque_moto']);			$this->typeMoto = utf8_encode($result['type_moto']);			$this->roleAsso = utf8_encode($result['role_asso']);			$this->dateNaissance = utf8_encode($result['date_naissance']);			$this->photo = utf8_encode($result['photo']);			$this->sexe = utf8_encode($result['sexe']);			$this->dateCreation = utf8_encode($result['date_creation']);			$this->dateMaj = utf8_encode($result['date_maj']);			$this->typeMembre = utf8_encode($result['type_membre']);		}	}	public static function listing() {		$membres = array();		$pdo = PDO2::getInstance();		$requete = $pdo->prepare("SELECT * FROM membres");		$requete->execute();		while ($result = $requete->fetch(PDO::FETCH_ASSOC)) {			$membre = new membre($result['id']);			$membre->load();			$membres[] = $membre;		}		$requete->closeCursor();				return $membres;	}			public function getProperties() {		return get_object_vars($this);	}	}